mapper.select:
SELECT count(*) FROM questions


        AND level = #{level}

    AND subject_id = #{subjectId}
    AND deleted = FALSE;

SELECT
    (SELECT count(*) FROM questions


            AND level = #{level}

         AND deleted = false
     )
        +
    (SELECT count(*) FROM my_questions


            AND level = #{level}

        AND user_id = #{userId}
        AND deleted = false
    )
AS total

SELECT * FROM questions

    AND subject_id = #{subjectId}

        AND level = #{level}

    AND deleted = false

ORDER BY RAND()
LIMIT #{number}

SELECT * FROM (
    SELECT q.subject_id, s.subject_name, q.question_id, NULL AS user_id, q.level, q.question_text, q.deleted, q.created_at, q.update_at FROM questions q
    JOIN SUBJECTS S ON q.subject_id = s.subject_id


            AND level = #{level}

        AND q.deleted = false


   UNION ALL

   SELECT NULL AS subject_id, NULL AS subject_name, question_id, user_id, level, question_text, deleted, created_at, update_at FROM my_questions


            AND level = #{level}

        AND user_id = #{userId}
        AND deleted = false

) AS combined_questions
ORDER BY RAND()
LIMIT #{number}

SELECT * FROM answers
WHERE question_id = #{questionId}
ORDER BY RAND()
