mapper.select:
SELECT * FROM my_questions

    AND user_id = #{userId}

        AND level = #{level}


        AND question_text LIKE CONCAT('%', #{searchKeyword}, '%')


ORDER BY question_id DESC
LIMIT #{offset}, #{pagePerItem}

SELECT count(*) FROM my_questions

    AND user_id = #{userId}

        AND level = #{level}


        AND question_text LIKE CONCAT('%', #{searchKeyword}, '%')

